bde.collectunitqty = 1 and bde.status in ('pending_source','confirming','claimed_ing','pending_approval','pending_negotiation','negotiating','designated_source')
and dde.source_unit_code in
#{item}
and bde.status in
#{item}
and (bde.name like concat('%', #{dto.keyword}, '%') or bde.definition like concat('%', #{dto.keyword}, '%') or bde.source_unit_name like concat('%', #{dto.keyword}, '%') or dde.source_unit_name like concat('%', #{dto.keyword}, '%') )
and bde.send_date between #{dto.sendDateBegin} and #{dto.sendDateEnd}
and bde.confirm_date between #{dto.confirmDateBegin} and #{dto.confirmDateEnd}
and bde.generatedatetime between #{dto.generatedatetimeBegin} and #{dto.generatedatetimeEnd}
order by ${sortSql}

select bde.data_element_id as dataElementId, bde.dataid as dataid, bde.datatype as datatype, bde.definition as definition, bde.send_date as receiveTime, bde.name as name, ct.status as status, ct.processing_date as processingDate from base_data_element bde left join confirmation_task ct on bde.dataid = ct.base_dataelement_dataid where ct.processing_unit_code = #{organCode,jdbcType=VARCHAR} and bde.status in ('pending_source','confirming','claimed_ing','pending_approval','pending_negotiation','negotiating','designated_source')
and ct.status = 'pending'
and ct.status in ('confirmed','rejected')
and bde.send_date between #{dto.sendDateBegin} and #{dto.sendDateEnd}
and ct.processing_date between #{dto.processDateBegin} and #{dto.processDateEnd}
and ct.status in
#{item}
and (bde.name like concat('%', #{dto.keyword}, '%') or bde.definition like concat('%', #{dto.keyword}, '%'))
order by ${sortSql}

SELECT bdl.dataid, bdl.name, bdl.definition, bdl.status, ct.processing_unit_code AS paUnitCode, ct.processing_unit_name AS paUnitName, bdl.send_date AS sendDate FROM base_data_element bdl LEFT JOIN confirmation_task ct ON bdl.dataid = ct.base_dataelement_dataid AND (ct.status in ('claimed', 'confirmed')) WHERE bdl.status = 'pending_approval'
AND bdl.send_date >= #{dto.sendDateBegin}
AND bdl.send_date <= #{dto.sendDateEnd}
AND (bdl.name LIKE CONCAT('%', #{dto.keyword}, '%') OR bdl.definition LIKE CONCAT('%', #{dto.keyword}, '%') OR ct.processing_unit_name LIKE CONCAT('%', #{dto.keyword}, '%'))
ORDER BY ${sortSql}
ORDER BY ct.processing_date DESC

SELECT bdl.dataid, bdl.name, bdl.definition, bdl.status, bdl.source_unit_code AS sourceUnitCode, bdl.source_unit_name AS sourceUnitName, bdl.source_unit_code AS paUnitCode, bdl.source_unit_name AS paUnitName, bdl.send_date AS sendDate, bdl.confirm_date AS confirmDate, bdl.collectunitqty, GROUP_CONCAT(DISTINCT dde.source_unit_name SEPARATOR '|') AS collectUnitName FROM base_data_element bdl LEFT JOIN domain_data_element dde ON bdl.dataid = dde.base_dataelement_dataid WHERE bdl.status = 'designated_source'
AND bdl.send_date >= #{dto.sendDateBegin}
AND bdl.send_date <= #{dto.sendDateEnd}
AND bdl.confirm_date >= #{dto.confirmDateBegin}
AND bdl.confirm_date <= #{dto.confirmDateEnd}
AND dde.source_unit_code IN
#{unitCode}
AND (bdl.name LIKE CONCAT('%', #{dto.keyword}, '%') OR bdl.definition LIKE CONCAT('%', #{dto.keyword}, '%') OR bdl.source_unit_name LIKE CONCAT('%', #{dto.keyword}, '%') OR dde.source_unit_name LIKE CONCAT('%', #{dto.keyword}, '%'))
GROUP BY bdl.dataid
ORDER BY ${sortSql}
ORDER BY bdl.confirm_date DESC

SELECT * FROM ( SELECT bde.dataid, bde.name, bde.definition, bde.datatype, bde.status as bde_status, ct.status as ct_status, bde.send_date, DATE_FORMAT(bde.confirm_date, '%Y-%m-%d %H:%i:%s') as confirm_date, bde.source_unit_code, bde.source_unit_name, ct.task_id, DATE_FORMAT(ct.processing_date, '%Y-%m-%d %H:%i:%s') AS processing_date FROM base_data_element bde LEFT JOIN confirmation_task ct ON bde.dataid = ct.base_dataelement_dataid
1=1 and bde.status in ('pending_source','confirming','claimed_ing','pending_approval','pending_negotiation','negotiating','designated_source')
AND (
OR (ct.status = 'pending') OR (ct.status = 'pending_claimed') OR (bde.status = 'pending_approval' AND ct.status = 'confirmed') OR (bde.status = 'pending_negotiation' AND ct.status = 'rejected') OR (bde.status IN ('claimed_ing','pending_approval','pending_negotiation') AND ct.status = 'claimed') OR (bde.status IN ('claimed_ing','pending_approval','pending_negotiation') AND ct.status = 'not_claimed') OR (bde.status = 'negotiating') OR (bde.status = 'designated_source') OR 1=0
)
AND ct.processing_unit_code = #{base.orgCode}
AND ct.send_date BETWEEN DATE(FROM_UNIXTIME(#{base.sendDateBegin} / 1000)) AND DATE(FROM_UNIXTIME(#{base.sendDateEnd} / 1000)) + INTERVAL 1 DAY
AND ct.processing_date BETWEEN DATE(FROM_UNIXTIME(#{base.processDateBegin} / 1000)) AND DATE(FROM_UNIXTIME(#{base.processDateEnd} / 1000)) + INTERVAL 1 DAY
AND bde.confirm_date BETWEEN DATE(FROM_UNIXTIME(#{base.receiveDateBegin} / 1000)) AND DATE(FROM_UNIXTIME(#{base.receiveDateEnd} / 1000)) + INTERVAL 1 DAY
AND (bde.name LIKE CONCAT('%', #{base.keyword}, '%') OR bde.definition LIKE CONCAT('%', #{base.keyword}, '%'))
order by ${base.sortSql}
) AS test

SELECT bde.dataid, bde.name, bde.definition, bde.datatype, bde.status as bde_status, ct.status as ct_status, bde.send_date, bde.source_unit_code, bde.source_unit_name, ct.task_id, DATE_FORMAT(ct.send_date, '%Y-%m-%d %H:%i:%s') AS recieve_date, DATE_FORMAT(ct.processing_date, '%Y-%m-%d %H:%i:%s') AS processing_date FROM base_data_element bde LEFT JOIN confirmation_task ct ON bde.dataid = ct.base_dataelement_dataid
AND ct.processing_unit_code = #{orgCode}
AND bde.dataid = #{dataid}
order by ct.processing_date desc limit 1

